import React from 'react';

// Function to properly format AI content and fix formatting issues
const formatAIContent = (content) => {
  if (!content) return '';
  
  // If content is already properly formatted HTML, return as-is
  if (content.includes('<div class="ai-') || content.includes('<h4>') || content.includes('<h5>')) {
    return content;
  }
  
  // For plain text responses that need formatting
  let formatted = content
    // Remove asterisks that aren't part of markdown formatting
    .replace(/\*+/g, '')
    // Clean up multiple spaces
    .replace(/\s+/g, ' ')
    // Trim whitespace
    .trim();
  
  // Don't break on colons that are part of labels/meanings
  // Only break on clear sentence endings followed by capital letters
  // But NOT when it's something like "Meaning: something" or "Analysis: text"
  const sentences = formatted.split(/(?<=\.)\s+(?=[A-Z][a-z])/);
  
  // Group sentences intelligently - don't break on colons
  const paragraphs = [];
  let currentParagraph = '';
  
  for (const sentence of sentences) {
    const trimmedSentence = sentence.trim();
    if (!trimmedSentence) continue;
    
    // If current paragraph is getting too long (over 300 chars), start a new one
    // But only if the current sentence doesn't start with a continuation word
    const continuationWords = ['However', 'Additionally', 'Furthermore', 'Moreover', 'Also', 'Therefore', 'Thus', 'For example', 'In addition'];
    const startsWithContinuation = continuationWords.some(word => trimmedSentence.startsWith(word));
    
    if (currentParagraph.length > 300 && !startsWithContinuation && !trimmedSentence.includes(':')) {
      if (currentParagraph) paragraphs.push(currentParagraph.trim());
      currentParagraph = trimmedSentence;
    } else {
      currentParagraph += (currentParagraph ? ' ' : '') + trimmedSentence;
    }
  }
  
  // Add the last paragraph
  if (currentParagraph) paragraphs.push(currentParagraph.trim());
  
  // If we only have one paragraph or the splitting didn't work well, keep as single block
  if (paragraphs.length <= 1 || paragraphs.some(p => p.length < 50)) {
    return `<p style="line-height: 1.6; margin-bottom: 12px;">${formatted}</p>`;
  }
  
  // Format as multiple paragraphs
  return paragraphs.map(paragraph => `<p style="line-height: 1.6; margin-bottom: 12px;">${paragraph}</p>`).join('');
};

const AIResults = ({ results, onClear, onRemoveCard }) => {
  if (!results || results.length === 0) return null;

  return (
    <div className="ai-results-section">
      <div className="ai-results-header">
        <h2>ðŸ§  AI Analysis Results</h2>
        <button className="ai-clear-all btn btn-secondary" onClick={onClear}>
          Clear All
        </button>
      </div>
      <div className="ai-cards-container">
        {results.map((result, index) => (
          <AICard 
            key={index} 
            result={result} 
            index={index}
            onRemove={onRemoveCard}
          />
        ))}
      </div>
    </div>
  );
};

const AICard = ({ result, index, onRemove }) => {
  return (
    <div className="ai-analysis-card">
      <div className="ai-card-header">
        <h3 className="ai-card-title">{result.title}</h3>
        <button 
          className="ai-card-close"
          onClick={() => onRemove && onRemove(index)}
          title="Remove this analysis"
        >
          Ã—
        </button>
      </div>
      <div className="ai-card-content">
        <div className="ai-content-wrapper" dangerouslySetInnerHTML={{ __html: formatAIContent(result.content) }} />
      </div>
    </div>
  );
};

export default AIResults;
