  setupEventListeners() {
    // Quick Guide button
    const guideBtn = document.getElementById('quick-guide-btn');
    if (guideBtn) {
      guideBtn.addEventListener('click', () => this.showQuickGuide());
    }

    // Close modal
    const closeBtn = document.getElementById('close-guide-modal');
    if (closeBtn) {
      closeBtn.addEventListener('click', () => this.hideQuickGuide());
    }

    // Restart tour
    const restartBtn = document.getElementById('restart-tour');
    if (restartBtn) {
      restartBtn.addEventListener('click', () => {
        this.hideQuickGuide();
        this.startTour();
      });
    }

    // Tour controls
    const skipBtn = document.getElementById('tour-skip');
    const nextBtn = document.getElementById('tour-next');
    if (skipBtn) {
      skipBtn.addEventListener('click', () => this.endTour());
    }
    if (nextBtn) {
      nextBtn.addEventListener('click', () => this.nextStep());
    }

    // Close modal when clicking outside
    const modal = document.getElementById('quick-guide-modal');
    if (modal) {
      modal.addEventListener('click', e => {
        if (e.target === modal) {
          this.hideQuickGuide();
        }
      });
    }

    // Close tour when clicking outside
    const overlay = document.getElementById('tour-overlay');
    if (overlay) {
      overlay.addEventListener('click', e => {
        if (e.target === overlay) {
          this.endTour();
        }
      });
    }

    // Mobile-specific event handlers
    if (window.innerWidth <= 768) {
      // Handle orientation change on mobile
      window.addEventListener('orientationchange', () => {
        setTimeout(() => {
          if (this.isGuideActive) {
            const step = this.tourSteps[this.currentStep];
            const target = document.querySelector(step.target);
            if (target) {
              this.positionSpotlight(target, step.position);
            }
          }
        }, 500);
      });

      // Handle window resize on mobile
      let resizeTimeout;
      window.addEventListener('resize', () => {
        clearTimeout(resizeTimeout);
        resizeTimeout = setTimeout(() => {
          if (this.isGuideActive) {
            const step = this.tourSteps[this.currentStep];
            const target = document.querySelector(step.target);
            if (target) {
              this.positionSpotlight(target, step.position);
            }
          }
        }, 300);
      });

      // Prevent zoom issues on mobile
      const tooltip = document.getElementById('tour-tooltip');
      if (tooltip) {
        tooltip.addEventListener('touchstart', e => {
          e.preventDefault();
        }, {
          passive: false
        });
      }
    }

    // Add scroll tracking for all devices
    this.setupScrollTracking();
  }
  setupScrollTracking() {
    // Remove existing scroll listener if any
    if (this.scrollListener) {
      window.removeEventListener('scroll', this.scrollListener);
      document.removeEventListener('scroll', this.scrollListener);
    }

    // Real-time scroll handler for immediate response
    this.scrollListener = () => {
      if (!this.isGuideActive || this.isRepositioning) return;

      // Use requestAnimationFrame for smooth 60fps updates
      if (this.animationFrame) {
        cancelAnimationFrame(this.animationFrame);
      }
      this.animationFrame = requestAnimationFrame(() => {
        const step = this.tourSteps[this.currentStep];
        const target = document.querySelector(step.target);
        if (target) {
          const spotlight = document.getElementById('tour-spotlight');
          const tooltip = document.getElementById('tour-tooltip');
          if (spotlight && tooltip) {
            const isMobile = window.innerWidth <= 768;
            const isSmallMobile = window.innerWidth <= 480;
            this.doPositionSpotlight(target, step.position, spotlight, tooltip, isMobile, isSmallMobile);
          }
        }
      });
    };

    // Add listeners to both window and document for better coverage
    window.addEventListener('scroll', this.scrollListener, {
      passive: true
    });
    document.addEventListener('scroll', this.scrollListener, {
      passive: true
    });
  }
  startTour() {
    this.isGuideActive = true;
    this.currentStep = 0;
    const overlay = document.getElementById('tour-overlay');
    if (overlay) {
      overlay.style.display = 'block';
      this.showStep();
    }
  }
  showStep() {
    const step = this.tourSteps[this.currentStep];
    const target = document.querySelector(step.target);
    if (!target) {
      // Try next step if target not found
      this.nextStep();
      return;
    }

    // Apply mobile-specific tooltip styles
    this.applyMobileTooltipStyles();

    // Update tooltip content
    document.getElementById('tour-title').textContent = step.title;
    document.getElementById('tour-content').innerHTML = step.content;
    document.getElementById('tour-progress').textContent = `${this.currentStep + 1} of ${this.tourSteps.length}`;

    // Update next button
    const nextBtn = document.getElementById('tour-next');
    if (this.currentStep === this.tourSteps.length - 1) {
      nextBtn.textContent = 'Finish';
    } else {
      nextBtn.textContent = 'Next';
    }

    // Position spotlight and tooltip
    setTimeout(() => {
      this.positionSpotlight(target, step.position);
    }, 100);
  }
  applyMobileTooltipStyles() {
    const isMobile = window.innerWidth <= 768;
    const isSmallMobile = window.innerWidth <= 480;
    if (!isMobile) return;
    const tooltip = document.getElementById('tour-tooltip');
    const tooltipBody = document.getElementById('tour-tooltip-body');
    const tooltipFooter = document.getElementById('tour-tooltip-footer');
    const title = document.getElementById('tour-title');
    const content = document.getElementById('tour-content');
    if (tooltip) {
      tooltip.style.maxWidth = isSmallMobile ? '95vw' : '90vw';
      tooltip.style.minWidth = isSmallMobile ? 'min(260px, 90vw)' : 'min(280px, 85vw)';
      tooltip.style.margin = isSmallMobile ? '8px' : '10px';
    }
    if (tooltipBody) {
      tooltipBody.style.padding = isSmallMobile ? '16px 20px 12px' : '18px 22px 14px';
    }
    if (tooltipFooter) {
      tooltipFooter.style.padding = isSmallMobile ? '0 16px 16px' : '0 20px 18px';
      tooltipFooter.style.display = 'flex';
      tooltipFooter.style.width = '100%';
      tooltipFooter.style.boxSizing = 'border-box';
      if (isSmallMobile) {
        tooltipFooter.style.flexDirection = 'column';
        tooltipFooter.style.gap = '12px';
        tooltipFooter.style.alignItems = 'stretch';
      } else {
        // Reset to default for tablets and desktop
        tooltipFooter.style.flexDirection = 'row';
        tooltipFooter.style.alignItems = 'center';
        tooltipFooter.style.justifyContent = 'space-between';
      }
    }
    if (title) {
      title.style.fontSize = isSmallMobile ? '16px' : '17px';
    }
    if (content) {
      content.style.fontSize = isSmallMobile ? '13px' : '14px';
      content.style.lineHeight = '1.4';
    }

    // Adjust button sizes for mobile
    const skipBtn = document.getElementById('tour-skip');
    const nextBtn = document.getElementById('tour-next');
    const progressSpan = document.getElementById('tour-progress');
    if (skipBtn && nextBtn) {
      // Get buttons container
      const buttonsContainer = skipBtn.parentElement;
      if (isSmallMobile) {
        // Mobile phone layout - stack buttons vertically with better visibility
        skipBtn.style.cssText = 'padding: 12px 20px; font-size: 15px; min-width: 120px; width: 100%; margin: 0; border: 2px solid #B6B09F; background: #F2F2F2; color: #000000; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box; font-weight: 600; box-shadow: 0 2px 8px rgba(0,0,0,0.1);';
        nextBtn.style.cssText = 'padding: 12px 20px; font-size: 15px; min-width: 120px; width: 100%; margin: 0; background: linear-gradient(135deg, #000000 0%, #B6B09F 100%); color: #F2F2F2; border: none; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box; font-weight: 600; box-shadow: 0 2px 8px rgba(0,0,0,0.2);';

        // Configure buttons container for mobile
        if (buttonsContainer) {
          buttonsContainer.style.display = 'flex';
          buttonsContainer.style.flexDirection = 'column';
          buttonsContainer.style.gap = '12px';
          buttonsContainer.style.width = '100%';
          buttonsContainer.style.alignItems = 'stretch';
          buttonsContainer.style.justifyContent = 'center';
        }
      } else if (isMobile) {
        // Tablet layout - side by side but larger and more visible
        skipBtn.style.cssText = 'padding: 10px 18px; font-size: 14px; min-width: 90px; margin: 0; border: 2px solid #B6B09F; background: #F2F2F2; color: #000000; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box; font-weight: 500; box-shadow: 0 2px 6px rgba(0,0,0,0.1);';
        nextBtn.style.cssText = 'padding: 10px 18px; font-size: 14px; min-width: 90px; margin: 0; background: linear-gradient(135deg, #000000 0%, #B6B09F 100%); color: #F2F2F2; border: none; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box; font-weight: 500; box-shadow: 0 2px 6px rgba(0,0,0,0.15);';

        // Configure buttons container for tablet
        if (buttonsContainer) {
          buttonsContainer.style.display = 'flex';
          buttonsContainer.style.flexDirection = 'row';
          buttonsContainer.style.gap = '12px';
          buttonsContainer.style.justifyContent = 'center';
          buttonsContainer.style.alignItems = 'center';
        }
      } else {
        // Desktop layout - original styling
        skipBtn.style.cssText = 'padding: 8px 16px; font-size: 13px; min-width: 70px; border: 2px solid #B6B09F; background: #F2F2F2; color: #000000; border-radius: 8px; cursor: pointer; transition: all 0.3s ease;';
        nextBtn.style.cssText = 'padding: 8px 16px; font-size: 13px; min-width: 70px; background: linear-gradient(135deg, #000000 0%, #B6B09F 100%); color: #F2F2F2; border: none; border-radius: 8px; cursor: pointer; transition: all 0.3s ease;';

        // Configure buttons container for desktop
        if (buttonsContainer) {
          buttonsContainer.style.display = 'flex';
          buttonsContainer.style.flexDirection = 'row';
          buttonsContainer.style.gap = '8px';
        }
      }
    }

    // Adjust progress text for mobile
    if (progressSpan) {
      progressSpan.style.fontSize = isSmallMobile ? '11px' : '12px';
      if (isSmallMobile) {
        progressSpan.style.textAlign = 'center';
        progressSpan.style.width = '100%';
      }
    }
  }
  positionSpotlight(target, position) {
    const spotlight = document.getElementById('tour-spotlight');
    const tooltip = document.getElementById('tour-tooltip');
    if (!spotlight || !tooltip || !target) return;

    // Mobile detection
    const isMobile = window.innerWidth <= 768;
    const isSmallMobile = window.innerWidth <= 480;

    // Scroll target into view first (from working implementation)
    if (!this.isRepositioning) {
      this.isRepositioning = true;
      target.scrollIntoView({
        behavior: 'smooth',
        block: 'center'
      });

      // Position spotlight and tooltip after a short delay to ensure scroll is complete
      setTimeout(() => {
        this.doPositionSpotlight(target, position, spotlight, tooltip, isMobile, isSmallMobile);

        // Make sure spotlight is visible (from working implementation)
        spotlight.style.display = 'block';
        spotlight.style.zIndex = '10000';
        this.isRepositioning = false;
      }, 300);
    } else {
      this.doPositionSpotlight(target, position, spotlight, tooltip, isMobile, isSmallMobile);
    }
  }
  doPositionSpotlight(target, position, spotlight, tooltip, isMobile, isSmallMobile) {
    // Get fresh coordinates after any scrolling
    const rect = target.getBoundingClientRect();
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const scrollLeft = window.pageXOffset || document.documentElement.scrollLeft;

    // Calculate spotlight dimensions (adapted from working quickdiff implementation)
    let spotlightRect = {
      left: rect.left,
      top: rect.top,
      width: rect.width,
      height: rect.height
    };
    let padding = isMobile ? isSmallMobile ? 8 : 10 : 12;

    // Enhanced handling for control groups (from working implementation)
    if (target.classList.contains('header-controls') || target.classList.contains('controls') || target.classList.contains('export-controls') || target.classList.contains('ai-controls') || target.classList.contains('main-controls') || target.id === 'aiControls') {
      const buttons = target.querySelectorAll('button:not([style*="display: none"]):not([hidden])');
      if (buttons.length > 0) {
        // Calculate the bounding box of all visible buttons
        let minLeft = Infinity,
          minTop = Infinity,
          maxRight = -Infinity,
          maxBottom = -Infinity;
        buttons.forEach(button => {
          const buttonRect = button.getBoundingClientRect();
          if (buttonRect.width > 0 && buttonRect.height > 0) {
            minLeft = Math.min(minLeft, buttonRect.left);
            minTop = Math.min(minTop, buttonRect.top);
            maxRight = Math.max(maxRight, buttonRect.right);
            maxBottom = Math.max(maxBottom, buttonRect.bottom);
          }
        });
        if (minLeft !== Infinity) {
          // Use the tighter bounding box for spotlight
          spotlightRect = {
            left: minLeft,
            top: minTop,
            width: maxRight - minLeft,
            height: maxBottom - minTop
          };
          padding = isMobile ? isSmallMobile ? 6 : 8 : 8; // Smaller padding for button groups
        }
      }
    }

    // Position spotlight with scroll offset (working implementation approach)
    spotlight.style.position = 'absolute';
    spotlight.style.left = spotlightRect.left + scrollLeft - padding + 'px';
    spotlight.style.top = spotlightRect.top + scrollTop - padding + 'px';
    spotlight.style.width = spotlightRect.width + padding * 2 + 'px';
    spotlight.style.height = spotlightRect.height + padding * 2 + 'px';

    // Position tooltip using working implementation approach with smart collision detection
    const tooltipRect = tooltip.getBoundingClientRect();
    let left, top;
    let finalPosition = position;

    // Smart positioning to avoid blocking spotlight
    const spotlightBounds = {
      left: spotlightRect.left,
      top: spotlightRect.top,
      right: spotlightRect.left + spotlightRect.width,
      bottom: spotlightRect.top + spotlightRect.height
    };

    // Check if tooltip would overlap with spotlight and adjust position
    const checkOverlap = (testLeft, testTop, testWidth, testHeight) => {
      const tooltipBounds = {
        left: testLeft,
        top: testTop,
        right: testLeft + testWidth,
        bottom: testTop + testHeight
      };
      return !(tooltipBounds.right < spotlightBounds.left || tooltipBounds.left > spotlightBounds.right || tooltipBounds.bottom < spotlightBounds.top || tooltipBounds.top > spotlightBounds.bottom);
    };

    // Calculate initial position
    let testLeft, testTop;
    switch (position) {
      case 'top':
        testLeft = rect.left + rect.width / 2 - tooltipRect.width / 2;
        testTop = rect.top - tooltipRect.height - 20;
        break;
      case 'bottom':
      default:
        testLeft = rect.left + rect.width / 2 - tooltipRect.width / 2;
        testTop = rect.bottom + 20;
        break;
      case 'left':
        testLeft = rect.left - tooltipRect.width - 20;
        testTop = rect.top + rect.height / 2 - tooltipRect.height / 2;
        break;
      case 'right':
        testLeft = rect.right + 20;
        testTop = rect.top + rect.height / 2 - tooltipRect.height / 2;
        break;
    }

    // Check for overlap and try alternative positions
    if (checkOverlap(testLeft, testTop, tooltipRect.width, tooltipRect.height)) {
      const alternatives = [];

      // Try all four positions and rank them by distance from spotlight
      const positions = ['top', 'bottom', 'left', 'right'];
      positions.forEach(pos => {
        let altLeft, altTop;
        switch (pos) {
          case 'top':
            altLeft = rect.left + rect.width / 2 - tooltipRect.width / 2;
            altTop = rect.top - tooltipRect.height - 20;
            break;
          case 'bottom':
            altLeft = rect.left + rect.width / 2 - tooltipRect.width / 2;
            altTop = rect.bottom + 20;
            break;
          case 'left':
            altLeft = rect.left - tooltipRect.width - 20;
            altTop = rect.top + rect.height / 2 - tooltipRect.height / 2;
            break;
          case 'right':
            altLeft = rect.right + 20;
            altTop = rect.top + rect.height / 2 - tooltipRect.height / 2;
            break;
        }

        // Check if this position fits in viewport
        const fitsInViewport = altLeft >= 0 && altTop >= 0 && altLeft + tooltipRect.width <= window.innerWidth && altTop + tooltipRect.height <= window.innerHeight;
        if (fitsInViewport && !checkOverlap(altLeft, altTop, tooltipRect.width, tooltipRect.height)) {
          // Calculate distance from spotlight center for ranking
          const spotlightCenterX = spotlightBounds.left + spotlightRect.width / 2;
          const spotlightCenterY = spotlightBounds.top + spotlightRect.height / 2;
          const tooltipCenterX = altLeft + tooltipRect.width / 2;
          const tooltipCenterY = altTop + tooltipRect.height / 2;
          const distance = Math.sqrt(Math.pow(tooltipCenterX - spotlightCenterX, 2) + Math.pow(tooltipCenterY - spotlightCenterY, 2));
          alternatives.push({
            position: pos,
            left: altLeft,
            top: altTop,
            distance: distance
          });
        }
      });

      // Use the best alternative position (closest to spotlight without overlapping)
      if (alternatives.length > 0) {
        alternatives.sort((a, b) => a.distance - b.distance);
        const best = alternatives[0];
        finalPosition = best.position;
        left = best.left;
        top = best.top;
      } else {
        // Fallback: push tooltip away from spotlight center
        const spotlightCenterX = spotlightBounds.left + spotlightRect.width / 2;
        const spotlightCenterY = spotlightBounds.top + spotlightRect.height / 2;
        const tooltipCenterX = testLeft + tooltipRect.width / 2;
        const tooltipCenterY = testTop + tooltipRect.height / 2;

        // Push away from spotlight
        const pushDistance = Math.max(tooltipRect.width, tooltipRect.height) / 2 + 30;
        if (tooltipCenterX < spotlightCenterX) {
          left = spotlightBounds.left - tooltipRect.width - 30;
        } else {
          left = spotlightBounds.right + 30;
        }
        top = testTop;
        finalPosition = tooltipCenterX < spotlightCenterX ? 'left' : 'right';
      }
    } else {
      // Original position is fine
      left = testLeft;
      top = testTop;
    }

    // Add scroll offset to tooltip position (working implementation)
    left += scrollLeft;
    top += scrollTop;

    // Adjust if tooltip goes off screen (considering scroll) - working implementation
    const margin = isMobile ? isSmallMobile ? 12 : 16 : 20;
    if (left < scrollLeft + margin) left = scrollLeft + margin;
    if (left + tooltipRect.width > scrollLeft + window.innerWidth - margin) {
      left = scrollLeft + window.innerWidth - tooltipRect.width - margin;
    }
    if (top < scrollTop + margin) top = scrollTop + margin;
    if (top + tooltipRect.height > scrollTop + window.innerHeight - margin) {
      top = scrollTop + window.innerHeight - tooltipRect.height - margin;
    }

    // Set tooltip position with absolute positioning (working implementation)
    tooltip.style.position = 'absolute';
    tooltip.style.left = left + 'px';
    tooltip.style.top = top + 'px';

    // Mobile-specific adjustments
    if (isMobile) {
      const tooltipWidth = isSmallMobile ? Math.min(260, window.innerWidth - 24) : Math.min(280, window.innerWidth - 32);
      tooltip.style.maxWidth = tooltipWidth + 'px';
      tooltip.style.width = tooltipWidth + 'px';
    }

    // Add mobile-specific spotlight styling
    if (isMobile) {
      spotlight.style.borderWidth = isSmallMobile ? '3px' : '2px';
      spotlight.style.boxShadow = isSmallMobile ? '0 0 0 9999px rgba(0, 0, 0, 0.9), 0 0 20px #B6B09F, inset 0 0 20px rgba(182, 176, 159, 0.4)' : '0 0 0 9999px rgba(0, 0, 0, 0.85), 0 0 15px #B6B09F, inset 0 0 15px rgba(182, 176, 159, 0.3)';
    }
  }
  nextStep() {
    if (this.currentStep < this.tourSteps.length - 1) {
      this.currentStep++;
      this.showStep();
    } else {
      this.endTour();
    }
  }
  endTour() {
    this.isGuideActive = false;
    const overlay = document.getElementById('tour-overlay');
    if (overlay) {
      overlay.style.display = 'none';
    }

    // Clean up scroll listener and animation frames
    if (this.scrollListener) {
      window.removeEventListener('scroll', this.scrollListener);
      document.removeEventListener('scroll', this.scrollListener);
      this.scrollListener = null;
    }
    if (this.animationFrame) {
      cancelAnimationFrame(this.animationFrame);
      this.animationFrame = null;
    }
    localStorage.setItem('quickdiff-tour-shown', 'true');
  }
  showQuickGuide() {
    const modal = document.getElementById('quick-guide-modal');
    if (modal) {
      modal.style.display = 'flex';
    }
  }
  hideQuickGuide() {
    const modal = document.getElementById('quick-guide-modal');
    if (modal) {
      modal.style.display = 'none';
    }
  }

  // Cleanup method for React component unmounting
  destroy() {
    this.endTour();

    // Remove all event listeners
    if (this.scrollListener) {
      window.removeEventListener('scroll', this.scrollListener);
    }

    // Remove DOM elements
    const overlay = document.getElementById('tour-overlay');
    const modal = document.getElementById('quick-guide-modal');
    const button = document.getElementById('quick-guide-btn');
    const styles = document.getElementById('quick-guide-dynamic-styles');
    if (overlay) overlay.remove();
    if (modal) modal.remove();
    if (button) button.remove();
    if (styles) styles.remove();
  }
}

// Export for React usage
export default ReactUserGuide;

// Make available globally
if (typeof window !== 'undefined') {
  window.ReactUserGuide = ReactUserGuide;
}

// Auto-initialize for React
if (typeof document !== 'undefined') {
  document.addEventListener('DOMContentLoaded', () => {
    if (!window.userGuideInitialized) {
      new ReactUserGuide();
      window.userGuideInitialized = true;
    }
  });
}

