        }
        
        // Adjust progress text for mobile
        if (progressSpan) {
            progressSpan.style.fontSize = isSmallMobile ? '11px' : '12px';
            if (isSmallMobile) {
                progressSpan.style.textAlign = 'center';
                progressSpan.style.width = '100%';
