  setupEventListeners() {
    // Quick Guide button
    const guideBtn = document.getElementById('quick-guide-btn');
    if (guideBtn) {
      guideBtn.addEventListener('click', () => this.showQuickGuide());
    }

    // Close modal
    const closeBtn = document.getElementById('close-guide-modal');
    if (closeBtn) {
      closeBtn.addEventListener('click', () => this.hideQuickGuide());
    }

    // Restart tour
    const restartBtn = document.getElementById('restart-tour');
    if (restartBtn) {
      restartBtn.addEventListener('click', () => {
        this.hideQuickGuide();
        this.startTour();
      });
    }

    // Tour controls
    const skipBtn = document.getElementById('tour-skip');
    const nextBtn = document.getElementById('tour-next');
    if (skipBtn) {
      skipBtn.addEventListener('click', () => this.endTour());
    }
    if (nextBtn) {
      nextBtn.addEventListener('click', () => this.nextStep());
    }

    // Close modal when clicking outside
    const modal = document.getElementById('quick-guide-modal');
    if (modal) {
      modal.addEventListener('click', e => {
        if (e.target === modal) {
          this.hideQuickGuide();
        }
      });
    }

    // Close tour when clicking outside
    const overlay = document.getElementById('tour-overlay');
    if (overlay) {
      overlay.addEventListener('click', e => {
        if (e.target === overlay) {
          this.endTour();
        }
      });
    }

    // Mobile-specific event handlers
    if (window.innerWidth <= 768) {
      // Handle orientation change on mobile
      window.addEventListener('orientationchange', () => {
        setTimeout(() => {
          if (this.isGuideActive) {
            const step = this.tourSteps[this.currentStep];
            const target = document.querySelector(step.target);
            if (target) {
              this.positionSpotlight(target, step.position);
            }
          }
        }, 500);
      });

      // Handle window resize on mobile
      let resizeTimeout;
      window.addEventListener('resize', () => {
        clearTimeout(resizeTimeout);
        resizeTimeout = setTimeout(() => {
          if (this.isGuideActive) {
            const step = this.tourSteps[this.currentStep];
            const target = document.querySelector(step.target);
            if (target) {
              this.positionSpotlight(target, step.position);
            }
          }
        }, 300);
      });

      // Prevent zoom issues on mobile
      const tooltip = document.getElementById('tour-tooltip');
      if (tooltip) {
        tooltip.addEventListener('touchstart', e => {
          e.preventDefault();
        }, {
          passive: false
        });
      }
    }

    // Add scroll tracking for all devices
    this.setupScrollTracking();
  }
  setupScrollTracking() {
    // Remove existing scroll listener if any
    if (this.scrollListener) {
      window.removeEventListener('scroll', this.scrollListener);
      document.removeEventListener('scroll', this.scrollListener);
    }

    // Real-time scroll handler for immediate response
    this.scrollListener = () => {
      if (!this.isGuideActive || this.isRepositioning) return;

      // Use requestAnimationFrame for smooth 60fps updates
      if (this.animationFrame) {
        cancelAnimationFrame(this.animationFrame);
      }
      this.animationFrame = requestAnimationFrame(() => {
        const step = this.tourSteps[this.currentStep];
        const target = document.querySelector(step.target);
        if (target) {
          const spotlight = document.getElementById('tour-spotlight');
          const tooltip = document.getElementById('tour-tooltip');
          if (spotlight && tooltip) {
            const isMobile = window.innerWidth <= 768;
            const isSmallMobile = window.innerWidth <= 480;
            this.doPositionSpotlight(target, step.position, spotlight, tooltip, isMobile, isSmallMobile);
          }
        }
      });
    };

    // Add listeners to both window and document for better coverage
    window.addEventListener('scroll', this.scrollListener, {
      passive: true
    });
    document.addEventListener('scroll', this.scrollListener, {
      passive: true
    });
  }
  startTour() {
    this.isGuideActive = true;
    this.currentStep = 0;
    const overlay = document.getElementById('tour-overlay');
    if (overlay) {
      overlay.style.display = 'block';
      this.showStep();
    }
  }
  showStep() {
    const step = this.tourSteps[this.currentStep];
    const target = document.querySelector(step.target);
    if (!target) {
      // Try next step if target not found
      this.nextStep();
      return;
    }

    // Apply mobile-specific tooltip styles
    this.applyMobileTooltipStyles();

    // Update tooltip content
    document.getElementById('tour-title').textContent = step.title;
    document.getElementById('tour-content').innerHTML = step.content;
    document.getElementById('tour-progress').textContent = `${this.currentStep + 1} of ${this.tourSteps.length}`;

    // Update next button
    const nextBtn = document.getElementById('tour-next');
    if (this.currentStep === this.tourSteps.length - 1) {
      nextBtn.textContent = 'Finish';
    } else {
      nextBtn.textContent = 'Next';
    }

    // Position spotlight and tooltip
    setTimeout(() => {
      this.positionSpotlight(target, step.position);
    }, 100);
  }
  applyMobileTooltipStyles() {
    const isMobile = window.innerWidth <= 768;
    const isSmallMobile = window.innerWidth <= 480;
    if (!isMobile) return;
    const tooltip = document.getElementById('tour-tooltip');
    const tooltipBody = document.getElementById('tour-tooltip-body');
    const tooltipFooter = document.getElementById('tour-tooltip-footer');
    const title = document.getElementById('tour-title');
    const content = document.getElementById('tour-content');
    if (tooltip) {
      tooltip.style.maxWidth = isSmallMobile ? '95vw' : '90vw';
      tooltip.style.minWidth = isSmallMobile ? 'min(260px, 90vw)' : 'min(280px, 85vw)';
      tooltip.style.margin = isSmallMobile ? '8px' : '10px';
    }
    if (tooltipBody) {
      tooltipBody.style.padding = isSmallMobile ? '16px 20px 12px' : '18px 22px 14px';
    }
    if (tooltipFooter) {
      tooltipFooter.style.padding = isSmallMobile ? '0 16px 16px' : '0 20px 18px';
      tooltipFooter.style.display = 'flex';
      tooltipFooter.style.width = '100%';
      tooltipFooter.style.boxSizing = 'border-box';
      if (isSmallMobile) {
        tooltipFooter.style.flexDirection = 'column';
        tooltipFooter.style.gap = '12px';
        tooltipFooter.style.alignItems = 'stretch';
      } else {
        // Reset to default for tablets and desktop
        tooltipFooter.style.flexDirection = 'row';
        tooltipFooter.style.alignItems = 'center';
        tooltipFooter.style.justifyContent = 'space-between';
      }
    }
    if (title) {
      title.style.fontSize = isSmallMobile ? '16px' : '17px';
    }
    if (content) {
      content.style.fontSize = isSmallMobile ? '13px' : '14px';
      content.style.lineHeight = '1.4';
    }

    // Adjust button sizes for mobile
    const skipBtn = document.getElementById('tour-skip');
    const nextBtn = document.getElementById('tour-next');
    const progressSpan = document.getElementById('tour-progress');
    if (skipBtn && nextBtn) {
      // Get buttons container
      const buttonsContainer = skipBtn.parentElement;
      if (isSmallMobile) {
        // Mobile phone layout - stack buttons vertically
        skipBtn.style.cssText = 'padding: 10px 16px; font-size: 14px; min-width: 100px; width: 100%; margin: 0; border: 2px solid #B6B09F; background: #F2F2F2; color: #000000; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box;';
        nextBtn.style.cssText = 'padding: 10px 16px; font-size: 14px; min-width: 100px; width: 100%; margin: 0; background: linear-gradient(135deg, #000000 0%, #B6B09F 100%); color: #F2F2F2; border: none; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box;';

        // Configure buttons container for mobile
        if (buttonsContainer) {
          buttonsContainer.style.display = 'flex';
          buttonsContainer.style.flexDirection = 'column';
          buttonsContainer.style.gap = '10px';
          buttonsContainer.style.width = '100%';
          buttonsContainer.style.alignItems = 'stretch';
        }
      } else if (isMobile) {
        // Tablet layout - side by side but larger
        skipBtn.style.cssText = 'padding: 9px 16px; font-size: 13px; min-width: 80px; margin: 0 4px; border: 2px solid #B6B09F; background: #F2F2F2; color: #000000; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box;';
        nextBtn.style.cssText = 'padding: 9px 16px; font-size: 13px; min-width: 80px; margin: 0 4px; background: linear-gradient(135deg, #000000 0%, #B6B09F 100%); color: #F2F2F2; border: none; border-radius: 8px; cursor: pointer; transition: all 0.3s ease; box-sizing: border-box;';

        // Configure buttons container for tablet
        if (buttonsContainer) {
          buttonsContainer.style.display = 'flex';
          buttonsContainer.style.flexDirection = 'row';
          buttonsContainer.style.gap = '8px';
          buttonsContainer.style.justifyContent = 'center';
        }
      } else {
        // Desktop layout - original styling
        skipBtn.style.cssText = 'padding: 8px 16px; font-size: 13px; min-width: 70px; border: 2px solid #B6B09F; background: #F2F2F2; color: #000000; border-radius: 8px; cursor: pointer; transition: all 0.3s ease;';
        nextBtn.style.cssText = 'padding: 8px 16px; font-size: 13px; min-width: 70px; background: linear-gradient(135deg, #000000 0%, #B6B09F 100%); color: #F2F2F2; border: none; border-radius: 8px; cursor: pointer; transition: all 0.3s ease;';

        // Configure buttons container for desktop
        if (buttonsContainer) {
          buttonsContainer.style.display = 'flex';
          buttonsContainer.style.flexDirection = 'row';
          buttonsContainer.style.gap = '8px';
        }
      }
    }

    // Adjust progress text for mobile
    if (progressSpan) {
      progressSpan.style.fontSize = isSmallMobile ? '11px' : '12px';
      if (isSmallMobile) {
        progressSpan.style.textAlign = 'center';
        progressSpan.style.width = '100%';
      }
    }
  }
  positionSpotlight(target, position) {
    const spotlight = document.getElementById('tour-spotlight');
    const tooltip = document.getElementById('tour-tooltip');
    if (!spotlight || !tooltip || !target) return;

    // Mobile detection
    const isMobile = window.innerWidth <= 768;
    const isSmallMobile = window.innerWidth <= 480;

    // First, scroll target into view on mobile to ensure it's visible (only on initial positioning)
    if (isMobile && !this.isRepositioning) {
      this.isRepositioning = true;
      target.scrollIntoView({
        behavior: 'smooth',
        block: 'center',
        inline: 'center'
      });

      // Wait for scroll to complete before positioning
      setTimeout(() => {
        this.doPositionSpotlight(target, position, spotlight, tooltip, isMobile, isSmallMobile);
        this.isRepositioning = false;
      }, 300);
    } else {
      this.doPositionSpotlight(target, position, spotlight, tooltip, isMobile, isSmallMobile);
    }
  }
  doPositionSpotlight(target, position, spotlight, tooltip, isMobile, isSmallMobile) {
    // Get fresh coordinates after any scrolling
    const rect = target.getBoundingClientRect();
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const scrollLeft = window.pageXOffset || document.documentElement.scrollLeft;

    // Viewport dimensions
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;
    let padding = isMobile ? isSmallMobile ? 6 : 8 : 12;
    let spotlightRect = {
      left: rect.left,
      top: rect.top,
      width: rect.width,
      height: rect.height
    };

    // Enhanced handling for control groups on mobile
    if (target.classList.contains('header-controls') || target.classList.contains('controls') || target.classList.contains('export-controls') || target.classList.contains('ai-controls')) {
      const buttons = target.querySelectorAll('button:not([style*="display: none"]):not([hidden])');
      if (buttons.length > 0) {
        // Calculate bounding box of all visible buttons
        let minLeft = Infinity,
          minTop = Infinity,
          maxRight = -Infinity,
          maxBottom = -Infinity;
        buttons.forEach(button => {
          const buttonRect = button.getBoundingClientRect();
          if (buttonRect.width > 0 && buttonRect.height > 0) {
            minLeft = Math.min(minLeft, buttonRect.left);
            minTop = Math.min(minTop, buttonRect.top);
            maxRight = Math.max(maxRight, buttonRect.right);
            maxBottom = Math.max(maxBottom, buttonRect.bottom);
          }
        });
        if (minLeft !== Infinity) {
          spotlightRect = {
            left: minLeft,
            top: minTop,
            width: maxRight - minLeft,
            height: maxBottom - minTop
          };
          padding = isMobile ? isSmallMobile ? 4 : 6 : 8;
        }
      }
    }

    // Position spotlight with FIXED coordinates that lock to the element
    // Use absolute positioning relative to the document, not viewport
    const spotlightLeft = Math.max(0, spotlightRect.left + scrollLeft - padding);
    const spotlightTop = Math.max(0, spotlightRect.top + scrollTop - padding);
    const spotlightWidth = Math.min(spotlightRect.width + padding * 2, viewportWidth);
    const spotlightHeight = Math.min(spotlightRect.height + padding * 2, viewportHeight);

    // Ensure spotlight uses fixed positioning to stay locked during scroll
    spotlight.style.position = 'fixed';
    spotlight.style.left = spotlightRect.left - padding + 'px';
    spotlight.style.top = spotlightRect.top - padding + 'px';
    spotlight.style.width = spotlightWidth + 'px';
    spotlight.style.height = spotlightHeight + 'px';

    // Mobile-optimized tooltip positioning with FIXED positioning
    const tooltipWidth = isMobile ? isSmallMobile ? Math.min(260, viewportWidth - 16) : Math.min(280, viewportWidth - 20) : 350;
    const tooltipHeight = isMobile ? 180 : 220; // More accurate tooltip height estimate

    let left, top;
    const margin = isMobile ? isSmallMobile ? 8 : 10 : 20;

    // Smart positioning based on available space in viewport
    const spaceAbove = rect.top;
    const spaceBelow = viewportHeight - rect.bottom;
    const spaceLeft = rect.left;
    const spaceRight = viewportWidth - rect.right;

    // Determine best position based on available space
    let finalPosition = position;
    if (isMobile) {
      if (position === 'top' && spaceAbove < tooltipHeight + margin) {
        finalPosition = 'bottom';
      } else if (position === 'bottom' && spaceBelow < tooltipHeight + margin) {
        finalPosition = 'top';
      }
    }

    // Use FIXED positioning for tooltip to ensure it stays visible during scroll
    tooltip.style.position = 'fixed';
    switch (finalPosition) {
      case 'top':
        left = rect.left + rect.width / 2 - tooltipWidth / 2;
        top = rect.top - tooltipHeight - (isMobile ? 10 : 15);
        tooltip.style.transform = 'translateY(0)';
        break;
      case 'bottom':
      default:
        left = rect.left + rect.width / 2 - tooltipWidth / 2;
        top = rect.bottom + (isMobile ? 10 : 15);
        tooltip.style.transform = 'translateY(0)';
        break;
    }

    // Constrain to viewport bounds (no scroll offset needed for fixed positioning)
    left = Math.max(margin, Math.min(left, viewportWidth - tooltipWidth - margin));
    top = Math.max(margin, Math.min(top, viewportHeight - tooltipHeight - margin));

    // Final mobile adjustments for better visibility
    if (isMobile) {
      // Ensure tooltip is fully visible within viewport
      const tooltipRight = left + tooltipWidth;
      const tooltipBottom = top + tooltipHeight;
      if (tooltipRight > viewportWidth) {
        left = viewportWidth - tooltipWidth - margin;
      }
      if (left < margin) {
        left = margin;
      }
      if (tooltipBottom > viewportHeight) {
        top = viewportHeight - tooltipHeight - margin;
      }
      if (top < margin) {
        top = margin;
      }

      // Ensure minimum safe distance from screen edges on mobile
      const safeMargin = isSmallMobile ? 12 : 16;
      left = Math.max(safeMargin, Math.min(left, viewportWidth - tooltipWidth - safeMargin));
      top = Math.max(safeMargin, Math.min(top, viewportHeight - tooltipHeight - safeMargin));

      // Set mobile-specific styles
      tooltip.style.maxWidth = tooltipWidth + 'px';
      tooltip.style.width = tooltipWidth + 'px';
    }
    tooltip.style.left = left + 'px';
    tooltip.style.top = top + 'px';

    // Add mobile-specific spotlight styling
    if (isMobile) {
      spotlight.style.borderWidth = isSmallMobile ? '3px' : '2px';
      spotlight.style.boxShadow = isSmallMobile ? '0 0 0 9999px rgba(0, 0, 0, 0.9), 0 0 20px #B6B09F, inset 0 0 20px rgba(182, 176, 159, 0.4)' : '0 0 0 9999px rgba(0, 0, 0, 0.85), 0 0 15px #B6B09F, inset 0 0 15px rgba(182, 176, 159, 0.3)';
    }
  }
  nextStep() {
    if (this.currentStep < this.tourSteps.length - 1) {
      this.currentStep++;
      this.showStep();
    } else {
      this.endTour();
    }
  }
  endTour() {
    this.isGuideActive = false;
    const overlay = document.getElementById('tour-overlay');
    if (overlay) {
      overlay.style.display = 'none';
    }

    // Clean up scroll listener and animation frames
    if (this.scrollListener) {
      window.removeEventListener('scroll', this.scrollListener);
      document.removeEventListener('scroll', this.scrollListener);
      this.scrollListener = null;
    }
    if (this.animationFrame) {
      cancelAnimationFrame(this.animationFrame);
      this.animationFrame = null;
    }
    localStorage.setItem('quickdiff-tour-shown', 'true');
  }
  showQuickGuide() {
    const modal = document.getElementById('quick-guide-modal');
    if (modal) {
      modal.style.display = 'flex';
    }
  }
  hideQuickGuide() {
    const modal = document.getElementById('quick-guide-modal');
    if (modal) {
      modal.style.display = 'none';
    }
  }

  // Cleanup method for React component unmounting
  destroy() {
    this.endTour();

    // Remove all event listeners
    if (this.scrollListener) {
      window.removeEventListener('scroll', this.scrollListener);
    }

    // Remove DOM elements
    const overlay = document.getElementById('tour-overlay');
    const modal = document.getElementById('quick-guide-modal');
    const button = document.getElementById('quick-guide-btn');
    const styles = document.getElementById('quick-guide-dynamic-styles');
    if (overlay) overlay.remove();
    if (modal) modal.remove();
    if (button) button.remove();
    if (styles) styles.remove();
  }
}

// Export for React usage
export default ReactUserGuide;

// Make available globally
if (typeof window !== 'undefined') {
  window.ReactUserGuide = ReactUserGuide;
}

// Auto-initialize for React
if (typeof document !== 'undefined') {
  document.addEventListener('DOMContentLoaded', () => {
    if (!window.userGuideInitialized) {
      new ReactUserGuide();
      window.userGuideInitialized = true;
    }
  });
}

