import React from 'react';

const Header = ({ onThemeToggle, onContrastToggle, onToggleInputMode, isInputOnlyMode }) => {
  return (
    <header className="header">
      <div className="title-section">
        <h1>‚ö° QuickDiff</h1>
        <p>Fast text comparison tool</p>
      </div>
      <div className="header-controls">
        <button 
          className="btn btn-secondary btn-icon-only" 
          onClick={onToggleInputMode}
          title={`${isInputOnlyMode ? 'Exit' : 'Enter'} Input-Only Mode (Ctrl+I)`}
        >
          {isInputOnlyMode ? 'üìù' : 'üîç'}
        </button>
        <button 
          className="btn btn-secondary btn-icon-only" 
          onClick={onContrastToggle}
          title="Toggle High Contrast"
        >
          üîÜ
        </button>
        <button 
          className="btn btn-secondary btn-icon-only" 
          onClick={onThemeToggle}
          title="Toggle Dark/Light Mode"
        >
          üåì
        </button>
      </div>
    </header>
  );
};

export default Header;
