import React, { useRef } from 'react';

const InputSection = ({ 
  originalText, 
  changedText, 
  onOriginalTextChange, 
  onChangedTextChange, 
  onFileLoad,
  showNotification 
}) => {
  const originalFileRef = useRef(null);
  const changedFileRef = useRef(null);

  const handleFileChange = async (event, target) => {
    const file = event.target.files[0];
    if (file) {
      // IMMEDIATE file size check BEFORE calling onFileLoad
      const isPDF = file.name.toLowerCase().endsWith('.pdf');
      const maxSize = isPDF ? 10 * 1024 * 1024 : 5 * 1024 * 1024; // 10MB for PDF, 5MB for others
      
      if (file.size > maxSize) {
        const sizeInMB = (file.size / 1024 / 1024).toFixed(1);
        const maxSizeMB = (maxSize / 1024 / 1024).toFixed(0);
        
        console.log(`üö´ FILE REJECTED IMMEDIATELY: ${file.name} is ${sizeInMB}MB (max: ${maxSizeMB}MB)`);
        
        // Reset input immediately
        event.target.value = '';
        
        // Show notification immediately
        if (showNotification) {
          showNotification(`‚ùå File too large: ${sizeInMB}MB (max: ${maxSizeMB}MB)`, 'error', 8000);
        }
        
        // Show alert immediately
        alert(`‚ùå FILE TOO LARGE!\n\nFile: ${file.name}\nSize: ${sizeInMB}MB\nMaximum allowed: ${maxSizeMB}MB\n\nPlease select a smaller file.`);
        
        // Don't call onFileLoad at all - complete rejection
        return;
      }
      
      try {
        await onFileLoad(file, target);
      } catch (error) {
        // Error already handled in handleFileLoad, just reset the input
        event.target.value = '';
        console.log('üö´ File load cancelled:', error.message);
        return false;
      }
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.currentTarget.classList.add('drag-over');
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.currentTarget.classList.remove('drag-over');
  };

  const handleDrop = async (e, target) => {
    e.preventDefault();
    e.currentTarget.classList.remove('drag-over');
    
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      
      // IMMEDIATE file size check BEFORE calling onFileLoad
      const isPDF = file.name.toLowerCase().endsWith('.pdf');
      const maxSize = isPDF ? 10 * 1024 * 1024 : 5 * 1024 * 1024; // 10MB for PDF, 5MB for others
      
      if (file.size > maxSize) {
        const sizeInMB = (file.size / 1024 / 1024).toFixed(1);
        const maxSizeMB = (maxSize / 1024 / 1024).toFixed(0);
        
        console.log(`üö´ FILE REJECTED IMMEDIATELY: ${file.name} is ${sizeInMB}MB (max: ${maxSizeMB}MB)`);
        
        // Show notification immediately
        if (showNotification) {
          showNotification(`‚ùå File too large: ${sizeInMB}MB (max: ${maxSizeMB}MB)`, 'error', 8000);
        }
        
        // Show alert immediately
        alert(`‚ùå FILE TOO LARGE!\n\nFile: ${file.name}\nSize: ${sizeInMB}MB\nMaximum allowed: ${maxSizeMB}MB\n\nPlease select a smaller file.`);
        
        // Don't call onFileLoad at all - complete rejection
        return;
      }
      
      try {
        await onFileLoad(file, target);
      } catch (error) {
        // Error already handled in handleFileLoad
        console.log('üö´ File drop cancelled:', error.message);
        return false;
      }
    }
  };

  return (
    <div className="input-section">
      <div className="input-group">
        <div className="input-header">
          <label htmlFor="originalText">Original Text</label>
          <div className="file-controls">
            <input 
              type="file" 
              ref={originalFileRef}
              accept=".txt,.md,.json,.html,.js,.py,.css,.xml,.csv,.log,.pdf" 
              style={{ display: 'none' }}
              onChange={(e) => handleFileChange(e, 'original')}
            />
            <button 
              className="btn btn-secondary btn-export" 
              onClick={() => originalFileRef.current?.click()}
            >
              üìÅ Load File
            </button>
          </div>
        </div>
        <div 
          className="textarea-container" 
          data-drop-zone="original"
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={(e) => handleDrop(e, 'original')}
        >
          <textarea 
            id="originalText"
            value={originalText}
            onChange={(e) => onOriginalTextChange(e.target.value)}
            placeholder="Paste your original text here or drag & drop a file..."
            rows="10"
          />
          <div className="drop-overlay">
            <div className="drop-message">
              <span>üìÅ</span>
              <p>Drop file here</p>
            </div>
          </div>
        </div>
      </div>
      
      <div className="input-group">
        <div className="input-header">
          <label htmlFor="changedText">Changed Text</label>
          <div className="file-controls">
            <input 
              type="file" 
              ref={changedFileRef}
              accept=".txt,.md,.json,.html,.js,.py,.css,.xml,.csv,.log,.pdf" 
              style={{ display: 'none' }}
              onChange={(e) => handleFileChange(e, 'changed')}
            />
            <button 
              className="btn btn-secondary btn-export" 
              onClick={() => changedFileRef.current?.click()}
            >
              üìÅ Load File
            </button>
          </div>
        </div>
        <div 
          className="textarea-container" 
          data-drop-zone="changed"
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={(e) => handleDrop(e, 'changed')}
        >
          <textarea 
            id="changedText"
            value={changedText}
            onChange={(e) => onChangedTextChange(e.target.value)}
            placeholder="Paste your changed text here or drag & drop a file..."
            rows="10"
          />
          <div className="drop-overlay">
            <div className="drop-message">
              <span>üìÅ</span>
              <p>Drop file here</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InputSection;
