import React from 'react';

// Function to format AI content like ChatGPT - clean and readable
const formatAIContent = (content) => {
  if (!content) return '';
  
  // Convert markdown-style formatting to HTML for proper display
  let formatted = content
    // Convert **text** to bold
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    // Convert bullet points with emojis
    .replace(/^‚Ä¢ (.*?)$/gm, '<div class="ai-bullet-point">‚Ä¢ $1</div>')
    // Convert numbered lists
    .replace(/^(\d+\.) (.*?)$/gm, '<div class="ai-numbered-item">$1 $2</div>')
    // Convert section headers with emojis
    .replace(/^(üìä|üìù|üß†|üé®|üèóÔ∏è|üìã|üîç|‚ú®|üéØ|üîß|üí°|üé≠|üßπ|üìÑ|üå°Ô∏è|‚öñÔ∏è|üîÑ|üåç|üî≤|üìè|üí¨|üßΩ|üìê) \*\*(.*?)\*\*$/gm, '<h3 class="ai-section-header"><span class="ai-emoji">$1</span> <strong>$2</strong></h3>')
    // Convert subsection headers
    .replace(/^\*\*(.*?):\*\*$/gm, '<h4 class="ai-subsection-header"><strong>$1:</strong></h4>')
    // Convert line breaks to proper spacing
    .replace(/\n\n/g, '</p><p class="ai-paragraph">')
    .replace(/\n/g, '<br/>');
  
  // Wrap in paragraph tags if not already wrapped
  if (!formatted.startsWith('<')) {
    formatted = '<p class="ai-paragraph">' + formatted + '</p>';
  }
  
  // Clean up any double paragraph tags
  formatted = formatted
    .replace(/<\/p><p class="ai-paragraph"><\/p>/g, '</p>')
    .replace(/<p class="ai-paragraph"><\/p>/g, '');
  
  return formatted;
};

const AIResults = ({ results, onClear, onRemoveCard }) => {
  if (!results || results.length === 0) return null;

  return (
    <div className="ai-results-section">
      <div className="ai-results-header">
        <h2>üß† AI Analysis Results</h2>
        <button className="ai-clear-all btn btn-secondary" onClick={onClear}>
          Clear All
        </button>
      </div>
      <div className="ai-cards-container">
        {results.map((result, index) => (
          <AICard 
            key={index} 
            result={result} 
            index={index}
            onRemove={onRemoveCard}
          />
        ))}
      </div>
    </div>
  );
};

const AICard = ({ result, index, onRemove }) => {
  return (
    <div className="ai-analysis-card">
      <div className="ai-card-header">
        <h3 className="ai-card-title">{result.title}</h3>
        <button 
          className="ai-card-close"
          onClick={() => onRemove && onRemove(index)}
          title="Remove this analysis"
        >
          √ó
        </button>
      </div>
      <div className="ai-card-content">
        <div className="ai-content-wrapper" dangerouslySetInnerHTML={{ __html: formatAIContent(result.content) }} />
      </div>
    </div>
  );
};

export default AIResults;
