import React from 'react';
import ChangeSummary from './ChangeSummary';
import DiffView from './DiffView';
import AIResults from './AIResults';
import Minimap from './Minimap';

const Results = ({
  diffResult,
  settings,
  changes,
  currentChangeIndex,
  minimapVisible,
  onNavigatePrevious,
  onNavigateNext,
  onToggleMinimap,
  onNavigateToChange,
  aiResults,
  showAiResults,
  onClearAIResults,
  onRemoveAICard
}) => {
  const [searchVisible, setSearchVisible] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [currentSearchIndex, setCurrentSearchIndex] = useState(0);
  const [zoomLevel, setZoomLevel] = useState(1.0);

  if (!diffResult) return null;

  // Enhanced navigation handlers
  const handleSearch = (term, options) => {
    // TODO: Implement search functionality
    console.log('Searching for:', term, options);
    setSearchResults([]);
  };

  const handleSearchNavigation = (direction) => {
    if (searchResults.length === 0) return;
    
    if (direction === 'next') {
      setCurrentSearchIndex((prev) => (prev + 1) % searchResults.length);
    } else {
      setCurrentSearchIndex((prev) => (prev - 1 + searchResults.length) % searchResults.length);
    }
  };

  const handleZoomIn = () => {
    setZoomLevel(prev => Math.min(prev + 0.1, 2.0));
  };

  const handleZoomOut = () => {
    setZoomLevel(prev => Math.max(prev - 0.1, 0.5));
  };

  const handleResetZoom = () => {
    setZoomLevel(1.0);
  };

  return (
    <div id="results-section" className="results-section" style={{ fontSize: `${zoomLevel}rem` }}>
      <ChangeSummary stats={diffResult.stats} />
      
      <div className="results-header">
        <h2>üîç Comparison Results</h2>
        <div className="diff-navigation">
          <button 
            className="btn btn-secondary btn-icon-only" 
            onClick={onNavigatePrevious}
            disabled={changes.length === 0}
            title="Previous Change (Ctrl+‚Üë)"
          >
            ‚¨ÜÔ∏è
          </button>
          <span className="change-counter">
            {changes.length === 0 ? '0 / 0' : `${currentChangeIndex + 1} / ${changes.length}`}
          </span>
          <button 
            className="btn btn-secondary btn-icon-only" 
            onClick={onNavigateNext}
            disabled={changes.length === 0}
            title="Next Change (Ctrl+‚Üì)"
          >
            ‚¨áÔ∏è
          </button>
          <div className="nav-divider"></div>
          <button 
            className="btn btn-secondary btn-export" 
            onClick={onToggleMinimap}
            title="Toggle Minimap (M)"
          >
            üó∫Ô∏è {minimapVisible ? 'Hide Map' : 'Show Map'}
          </button>
        </div>
      </div>
      
      {minimapVisible && (
        <Minimap 
          changes={changes} 
          currentChangeIndex={currentChangeIndex}
          onChangeClick={onNavigateToChange}
        />
      )}
      
      <DiffView 
        diffResult={diffResult}
        viewMode={settings.viewMode}
        currentChangeIndex={currentChangeIndex}
      />
      
      {showAiResults && (
        <AIResults 
          results={aiResults}
          onClear={onClearAIResults}
          onRemoveCard={onRemoveAICard}
        />
      )}

      {/* Enhanced Navigation Components */}
      <FloatingToolbar
        onNavigatePrevious={onNavigatePrevious}
        onNavigateNext={onNavigateNext}
        onToggleMinimap={onToggleMinimap}
        onToggleSearch={() => setSearchVisible(!searchVisible)}
        onZoomIn={handleZoomIn}
        onZoomOut={handleZoomOut}
        onResetZoom={handleResetZoom}
        changes={changes}
        currentChangeIndex={currentChangeIndex}
        minimapVisible={minimapVisible}
        searchVisible={searchVisible}
        zoomLevel={zoomLevel}
      />

      <SearchOverlay
        isVisible={searchVisible}
        onClose={() => setSearchVisible(false)}
        onSearch={handleSearch}
        searchResults={searchResults}
        currentResultIndex={currentSearchIndex}
        onNavigateResult={handleSearchNavigation}
      />
    </div>
  );
};

export default Results;
