import React, { useState, useEffect, useRef } from 'react';

const SearchOverlay = ({ 
  isVisible, 
  onClose, 
  onSearch, 
  searchResults, 
  currentResultIndex, 
  onNavigateResult 
}) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [caseSensitive, setCaseSensitive] = useState(false);
  const [wholeWords, setWholeWords] = useState(false);
  const inputRef = useRef(null);

  useEffect(() => {
    if (isVisible && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isVisible]);

  useEffect(() => {
    if (searchTerm) {
      onSearch(searchTerm, { caseSensitive, wholeWords });
    }
  }, [searchTerm, caseSensitive, wholeWords, onSearch]);

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      onClose();
    } else if (e.key === 'Enter') {
      if (e.shiftKey) {
        onNavigateResult('previous');
      } else {
        onNavigateResult('next');
      }
    }
  };

  if (!isVisible) return null;

  return (
    <div className="search-overlay">
      <div className="search-container">
        <div className="search-header">
          <h3>üîç Search in Diff</h3>
          <button className="search-close" onClick={onClose}>‚úï</button>
        </div>
        
        <div className="search-input-group">
          <input
            ref={inputRef}
            type="text"
            className="search-input"
            placeholder="Search text..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <div className="search-controls">
            <button 
              className="search-nav-btn"
              onClick={() => onNavigateResult('previous')}
              disabled={!searchResults.length}
              title="Previous (Shift+Enter)"
            >
              ‚¨ÜÔ∏è
            </button>
            <span className="search-counter">
              {searchResults.length === 0 
                ? '0 / 0' 
                : `${currentResultIndex + 1} / ${searchResults.length}`}
            </span>
            <button 
              className="search-nav-btn"
              onClick={() => onNavigateResult('next')}
              disabled={!searchResults.length}
              title="Next (Enter)"
            >
              ‚¨áÔ∏è
            </button>
          </div>
        </div>

        <div className="search-options">
          <label className="search-option">
            <input
              type="checkbox"
              checked={caseSensitive}
              onChange={(e) => setCaseSensitive(e.target.checked)}
            />
            Case sensitive
          </label>
          <label className="search-option">
            <input
              type="checkbox"
              checked={wholeWords}
              onChange={(e) => setWholeWords(e.target.checked)}
            />
            Whole words
          </label>
        </div>

        <div className="search-help">
          <small>
            Press <kbd>Enter</kbd> for next, <kbd>Shift+Enter</kbd> for previous, <kbd>Esc</kbd> to close
          </small>
        </div>
      </div>
    </div>
  );
};

export default SearchOverlay;
