import React, { useState, useEffect } from 'react';

const UserGuide = ({ isVisible: externalVisible, onHide }) => {
  const [internalVisible, setInternalVisible] = useState(false);
  const [currentStep, setCurrentStep] = useState(0);
  const [hasSeenGuide, setHasSeenGuide] = useState(false);
  
  const isVisible = externalVisible || internalVisible;

  const steps = [
    {
      title: "Welcome to QuickDiff! üöÄ",
      content: `
        <p>QuickDiff is a powerful text comparison tool that helps you find differences between texts quickly and efficiently.</p>
        <p><strong>Key Features:</strong></p>
        <ul>
          <li>üîç Multiple comparison modes (line, word, character)</li>
          <li>üé® Dark/Light themes with high contrast support</li>
          <li>ü§ñ AI-powered text analysis</li>
          <li>üìÅ File upload support</li>
          <li>üìä Export results in multiple formats</li>
          <li>‚å®Ô∏è Keyboard shortcuts for efficiency</li>
        </ul>
      `,
      target: ".quickdiff-app"
    },
    {
      title: "Settings Panel üõ†Ô∏è",
      content: `
        <p>Configure your comparison preferences:</p>
        <ul>
          <li><strong>Diff Mode:</strong> Choose between line-by-line, word-by-word, or character-level comparison</li>
          <li><strong>View Mode:</strong> Side-by-side or inline view</li>
          <li><strong>Language:</strong> Auto-detect or manually select programming language for syntax highlighting</li>
          <li><strong>Ignore Options:</strong> Skip case, whitespace, or punctuation differences</li>
          <li><strong>Live Preview:</strong> Automatically compare as you type</li>
        </ul>
      `,
      target: ".settings-panel"
    },
    {
      title: "Input Your Text üìù",
      content: `
        <p>Add your text to compare:</p>
        <ul>
          <li><strong>Type directly:</strong> Paste or type text in the text areas</li>
          <li><strong>Upload files:</strong> Click "Load File" or drag & drop files</li>
          <li><strong>Supported formats:</strong> .txt, .md, .json, .html, .js, .py, .css</li>
        </ul>
        <p><em>Tip: The app will auto-detect the programming language for better highlighting!</em></p>
      `,
      target: ".input-section"
    },
    {
      title: "Control Panel üéÆ",
      content: `
        <p>Use these controls to manage your comparison:</p>
        <ul>
          <li><strong>Compare Texts:</strong> Start the comparison process</li>
          <li><strong>Clear All:</strong> Remove all text and results</li>
          <li><strong>Swap Texts:</strong> Exchange original and changed text</li>
          <li><strong>Export:</strong> Save results as TXT, HTML, Markdown, or PDF</li>
          <li><strong>AI Tools:</strong> Get intelligent analysis of your text differences</li>
        </ul>
      `,
      target: ".controls"
    },
    {
      title: "Keyboard Shortcuts ‚å®Ô∏è",
      content: `
        <p>Speed up your workflow with these shortcuts:</p>
        <ul>
          <li><strong>Ctrl+Enter:</strong> Compare texts</li>
          <li><strong>Ctrl+K:</strong> Clear all content</li>
          <li><strong>Ctrl+S:</strong> Swap texts</li>
          <li><strong>Ctrl+C:</strong> Copy results (when visible)</li>
          <li><strong>Ctrl+D:</strong> Toggle dark/light theme</li>
          <li><strong>Ctrl+H:</strong> Toggle high contrast mode</li>
          <li><strong>Ctrl+M:</strong> Toggle minimap</li>
          <li><strong>Ctrl+‚Üë/‚Üì:</strong> Navigate between changes</li>
          <li><strong>Escape:</strong> Clear AI results</li>
        </ul>
      `,
      target: ".quickdiff-app"
    },
    {
      title: "AI Analysis ü§ñ",
      content: `
        <p>Get intelligent insights about your text differences:</p>
        <ul>
          <li><strong>Explain:</strong> Get detailed explanations of changes</li>
          <li><strong>Rewrite:</strong> Suggestions for improving the text</li>
          <li><strong>Summary:</strong> Quick overview of main changes</li>
          <li><strong>Tone:</strong> Analysis of writing tone and style</li>
          <li><strong>Cleanup:</strong> Recommendations for text cleanup</li>
        </ul>
        <p><em>Note: AI analysis provides simulated results for demonstration purposes.</em></p>
      `,
      target: ".controls"
    },
    {
      title: "You're All Set! üéâ",
      content: `
        <p>You're now ready to use QuickDiff effectively!</p>
        <p><strong>Quick Start:</strong></p>
        <ol>
          <li>Paste your original text in the left area</li>
          <li>Paste your changed text in the right area</li>
          <li>Click "Compare Texts" or press Ctrl+Enter</li>
          <li>Explore the results and use AI analysis for deeper insights</li>
        </ol>
        <p><strong>Need help?</strong> Click the ‚ùì button in the header anytime to see this guide again.</p>
        <p>Happy comparing! üöÄ</p>
      `,
      target: ".quickdiff-app"
    }
  ];

  useEffect(() => {
    // Check if user has seen the guide before
    const hasSeenBefore = localStorage.getItem('quickdiff_guide_seen');
    if (!hasSeenBefore) {
      // Show guide automatically for first-time users
      setTimeout(() => {
        setInternalVisible(true);
      }, 1000);
    }
    setHasSeenGuide(!!hasSeenBefore);
  }, []);

  const hideGuide = () => {
    setInternalVisible(false);
    if (onHide) onHide();
    // Mark as seen
    localStorage.setItem('quickdiff_guide_seen', 'true');
    setHasSeenGuide(true);
  };

  const nextStep = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    } else {
      hideGuide();
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const skipGuide = () => {
    hideGuide();
  };

  const getSpotlightElement = () => {
    const target = currentStepData.target;
    return document.querySelector(target);
  };

  const createSpotlight = () => {
    const element = getSpotlightElement();
    if (!element) return null;

    const rect = element.getBoundingClientRect();
    return {
      top: rect.top - 10,
      left: rect.left - 10,
      width: rect.width + 20,
      height: rect.height + 20
    };
  };

  if (!isVisible) {
    return null; // Button will be rendered in Header component
  }

  const currentStepData = steps[currentStep];

  const spotlight = createSpotlight();

  return (
    <>
      {/* Add CSS for pulse animation */}
      <style>{`
        @keyframes pulse {
          0% { box-shadow: 0 0 0 9999px rgba(0, 0, 0, 0.8), 0 0 20px #007bff; }
          50% { box-shadow: 0 0 0 9999px rgba(0, 0, 0, 0.8), 0 0 30px #007bff, 0 0 40px #007bff; }
          100% { box-shadow: 0 0 0 9999px rgba(0, 0, 0, 0.8), 0 0 20px #007bff; }
        }
      `}</style>
      
      {/* Guide Overlay with Spotlight */}
      <div className="user-guide-overlay" style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        zIndex: 10000,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        
        {/* Spotlight Effect */}
        {spotlight && (
          <div
            className="spotlight-cutout"
            style={{
              position: 'absolute',
              top: spotlight.top,
              left: spotlight.left,
              width: spotlight.width,
              height: spotlight.height,
              backgroundColor: 'transparent',
              border: '3px solid #007bff',
              borderRadius: '8px',
              boxShadow: '0 0 0 9999px rgba(0, 0, 0, 0.8), 0 0 20px #007bff',
              pointerEvents: 'none',
              zIndex: 10001,
              animation: 'pulse 2s infinite'
            }}
          />
        )}
        <div className="user-guide-modal" style={{
          backgroundColor: 'var(--bg-color, #ffffff)',
          color: 'var(--text-color, #000000)',
          borderRadius: '12px',
          padding: '30px',
          maxWidth: '600px',
          width: '90%',
          maxHeight: '80vh',
          overflow: 'auto',
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.3)',
          border: '2px solid var(--border-color, #e0e0e0)',
          position: 'relative',
          zIndex: 10002
        }}>
          <div className="user-guide-header" style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '20px',
            borderBottom: '2px solid var(--border-color, #e0e0e0)',
            paddingBottom: '15px'
          }}>
            <h2 style={{ margin: 0, fontSize: '24px' }}>{currentStepData.title}</h2>
            <button 
              onClick={hideGuide}
              style={{
                background: 'none',
                border: 'none',
                fontSize: '24px',
                cursor: 'pointer',
                color: 'var(--text-color, #666)',
                padding: '0',
                width: '30px',
                height: '30px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}
              title="Close Guide"
            >
              √ó
            </button>
          </div>

          <div className="user-guide-content" style={{
            marginBottom: '30px',
            lineHeight: '1.6'
          }}>
            <div dangerouslySetInnerHTML={{ __html: currentStepData.content }} />
          </div>

          <div className="user-guide-progress" style={{
            display: 'flex',
            justifyContent: 'center',
            marginBottom: '20px',
            gap: '8px'
          }}>
            {steps.map((_, index) => (
              <div
                key={index}
                style={{
                  width: '12px',
                  height: '12px',
                  borderRadius: '50%',
                  backgroundColor: index === currentStep ? 'var(--primary-color, #007bff)' : 'var(--border-color, #e0e0e0)',
                  transition: 'background-color 0.3s ease'
                }}
              />
            ))}
          </div>

          <div className="user-guide-footer" style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            gap: '15px'
          }}>
            <div style={{ display: 'flex', gap: '10px' }}>
              <button 
                onClick={prevStep}
                disabled={currentStep === 0}
                className="btn btn-secondary"
                style={{
                  opacity: currentStep === 0 ? 0.5 : 1,
                  cursor: currentStep === 0 ? 'not-allowed' : 'pointer'
                }}
              >
                ‚Üê Previous
              </button>
              <button 
                onClick={nextStep}
                className="btn btn-primary"
              >
                {currentStep === steps.length - 1 ? 'Finish' : 'Next ‚Üí'}
              </button>
            </div>

            <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
              <span style={{ 
                fontSize: '14px', 
                color: 'var(--text-secondary, #666)',
                marginRight: '10px'
              }}>
                {currentStep + 1} of {steps.length}
              </span>
              <button 
                onClick={skipGuide}
                className="btn btn-secondary"
                style={{ fontSize: '14px' }}
              >
                Skip Guide
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default UserGuide;
